INSERT INTO `jos_emundus_setup_checklist` (`id`, `whenneed`, `title`, `text`, `page`) VALUES
(1, 1, 'Your application is not yet complete', '<p><span style="background-color: #ffff00;"><strong>Please check and fill all required forms and/or upload all missing attachments. You will be able to send and print your application once it is complete.</strong></span></p>', 'checklist'),
(2, 0, 'Congratulations your application is complete!', '<p>We have now all the compulsory documents for receiving your application.</p><p><strong>Thanks for checking that you do not need to add specific-case documents (TOEFL scores for example).</strong> You can upload new files or send then print your application.</p>', 'checklist'),
(3, 0, 'Statement of understanding', '<strong><em><p>I declare that all the information in this application is true, accurate, and complete to the best of my knowledge.</p><p>I understand that withholding, misrepresenting, or giving false information will invalidate my application and make me ineligible for admission:</p></em></strong>', 'declaration'),
(4, 0, 'Instructions', '<ul style="text-align: justify;">\n</ul>\n<p>Select an item in the Menu and complete all fields marked by a red star on the corresponding page. Once you have completed a page, you may save and continue with another page. <em>Do not leave the page before saving its contents, or you will have to resubmit the data just entered.</em></p>\n<p><em>You are invited to upload documents in .pdf format (except for photos).</em> It is possible to also upload documents in .jpg, .jpeg format, as well as in .png and .gif format for pictures.The reference letters will be directly submitted by the referees you will have chosen.</p>\n<p>You may delete attachments if needed, <em>e.g. </em>to update them.</p>', 'instructions'),
(5, 2, 'Refused', 'We are sorry, you are not accepted for the master.', 'checklist'),
(6, 3, 'Waiting List', 'You are on the waiting list for the master acceptance.', 'checklist'),
(7, 4, 'Accepted', 'Congratulations! You are accepted for the master.', 'checklist');
INSERT INTO `jos_emundus_setup_emails` (`id`, `lbl`, `subject`, `emailfrom`, `message`, `name`) VALUES
(1, 'confirm_post', 'eMundus | Application form have been posted', 'info@emundus.fr', '<p>Dear [NAME],<br />I hereby confirm that we have received your application to the eMundus master course in electronic form. Please do not forget to send any necessary originals by regular post.<br /><br />The consortium will evaluate all complete applications and you will be informed by email of the outcome of this process.<br /><br />Sincerely,<br /><br /><br />eMundus coordinator</p>', 'International Student Application Office'),
(2, 'attachment', 'eMundus | Attachment have been uploaded', 'info@emundus.fr', '<p>Student id : [ID]  <br />Student First name and Name : [NAME] <br />Student email: [EMAIL]</p><p>Dear [NAME],</p><p>A new document has been uploaded on your personal page.</p><p>Best regards, <br />The administrative staff</p>', 'International Student Application Office'),
(3, 'candidature_accepted', 'eMundus | Candidature accepted', 'info@emundus.fr', '<p>Dear [NAME],<br /><br />The eMundus master commission has now considered all applications, and has made the final choice. Many very strong applications have been submitted to the programme and so it has not been an easy task.<br /><br />I am glad to inform you that we decided to offer you a scholarship.<br /><br />Please note that before we can send you a contract, we have to submit the list of students to whom  we offer a Mundus scholarship to the European Commission, who has to make some formal verifications. This will not affect our ranking, and in principle there should be no problem with this.<br /><br />If for some reason you decide not to accept our offer it would be kind of you to let us know as soon as possible, so that we can inform the students who are on the reserve list at this point.<br /><br />For a list of eMundus alumni, see www.emundus.eu<br /><br />Looking forward to meet you,<br /><br />eMundus coordinator</p>', 'International Student Application Office'),
(4, 'candidature_waiting_list', 'eMundus | Candidature On Waiting List', 'info@emundus.fr', '<p>Dear [NAME],<br /><br />The eMundus master commission has now considered all applications, and has made the final choice.<br />Many very strong applications have been submitted to the programme and so it has not been an easy task.<br /><br />I am sorry to inform you that we are not able to offer you a scholarship yet. Your name is on our reserve list.<br /><br />But not everything is lost. We''ll write again in case one of the students on the main list declines our offer of a scholarship.</p>\r\n<p>Of course, since your level has been considered high enough, you might also attend the eMundus course without benefiting from a scholarship.</p>\r\n<p><br />Please let us know if you plan to enrol in eMundus  even if you are not awarded a scholarship by the consortium.<br /><br />Sincerely,<br /><br />eMundus coordinator</p>', 'International Student Application Office'),
(6, 'candidature_rejected', 'eMundus | Candidature rejected', 'info@emundus.fr', '<p>Dear candidate,<br /><br />The eMundus master commission has now considered all applications, and has made the final choice.<br />Many very strong applications have been submitted to the programme and so it has not been an easy task.<br /><br />I am sorry to inform you that we are not in a position to accept you into the programme (this year).<br /><br /><br />Sincerely,<br /><br />eMundus coordinator</p>', 'International Student Application Office'),
(7, 'bank_letter', 'eMundus | Pré-ouverture de compte étudiant', 'info@emundus.fr', 'Student id : [ID] \r\nStudent First name and Name : [NAME] \r\nStudent email: [EMAIL] \r\n\r\nMadame, Monsieur, \r\n\r\nTous les documents pour pré-ouvrir le compte de cet étudiant sont maintenant présent dans son dossier.\r\nVous pouvez donc pré-ouvrir le compte.\r\n\r\nCordialement,', 'International Student Application Office'),
(8, 'referent_letter', 'eMundus | Referent', 'info@emundus.fr', '<p>Student id : [ID]<br />Student First name and Name : [NAME]<br />Student email: [EMAIL]</p>\r\n<p>Dear colleague,</p>\r\n<p>One of your student, [NAME] is applying for one of our MSc.You have been chosen by the above mentioned student to be his referee. We would greatly appreciate if you could fill in the attached recommendation form in support of this student''s application.</p>\r\n<p>The student’s application will not be considered until at least one letter of reference has been uploaded !</p>\r\n<p>To upload a letter is a very easy process: prepare the letter using the file attached. Print it, sign it and scan it. Then, go to the link below, and follow the instructions.<a href="[UPLOAD_URL]"><br />[UPLOAD_URL]</a></p>\r\n<p>Thanking you in advance for your kind cooperation, I send you my best regards.</p>\r\n<p>Sincerely,<br />The administrative staff</p>\r\n<p>P.S.: please note that you may only <a href="[UPLOAD_URL]">upload</a> <strong>one document</strong> for each student, but you may submit it in .zip format.</p>', 'International Student Application Office'),
(9, 'attachment_notification', 'eMundus  | Attachment have been uploaded for student', 'info@emundus.fr', '<p>Student id : [ID]  <br />Student First name and Name : [NAME]  <br />Student email: [EMAIL]</p><p>A new document has been uploaded on personal page of [NAME] :</p>', 'International Student Application Office'),
(10, 'scholarship_status', 'eMundus | Scholarship Status', 'info@emundus.fr', 'The answer to your scholarship application(s) is given above.\r\n\r\nWe would appreciate receiving acknowledgement of your acceptation of the scholarship.\r\n\r\nThe status of students on the waiting list will be upgraded in case of the withdrawal of candidate(s) and you will be immediately notified by e-mail. \r\n\r\nWe would appreciate to know (or to be reminded) whether your joining the programme depends on the scholarship.\r\n\r\nPlease recall your ID number and your name in the subject field of any e-mail you will send us.\r\n\r\nThank you for your interest in our Programme.\r\n\r\nSincerely,\r\n\r\nPS : At any time, you will also be able to come back on your login page and modify your personal information (e-mail addres, current address...). \r\n\r\nWe will ONLY use these adresses for the information we will send you by e-mail or by regular mail (such as your acceptation letter). So please check regularly that the data base has your current coordinates and update it if there is some change. \r\n\r\nAdministrative Staff', 'International Student Application Office'),
(11, 'register', 'eMundus | Welcome to the application platform', 'register@emundus.fr', '<p>Dear [NAME], <br /> <br />Thank you for your interest in one of the Groupe eMundus master course. We have received your request to submit an application to our programme and we will process it as soon as you confirm your email address by clicking on the following hyperlink: <a href="[ACTIVATION_URL]">[ACTIVATION_URL]</a><br /> <br />Once your email address is confirmed our moderators will be notified so that you may proceed to complete your application. <br /> <br />You will be notified by email of the progress of your application. <br /> <br />Kind Regards,</p>\r\n<p>eMundus coordinator</p>\r\n<p>Your username : [USERNAME] <br />Your password : [PASSWORD]</p>', 'International Student Application Office'),
(5, 'new_account', 'eMundus | Welcome to the eMundus platform', 'info@emundus.fr', '<p>Your user id : [ID]<br />Your name : [NAME] <br />Your email : [EMAIL] <br />Your username : [USERNAME] <br />Your password : [PASSWORD]</p>\r\n<p>Dear [NAME], <br />A new account has been created for you for the eMundus Master application plateforme : [SITE_URL]<br />Kind Regards, <br /> eMundus Website Administration Team</p>', 'International Student Application Office'),
(12, 'assessors_set', 'eMundus | Applicant list', 'info@emundus.fr', '<p>Dear [NAME],<br />\r\nYou have been affected to evaluate a list of applicants. [APPLICANTS_LIST]Evaluation criterias :[EVAL_CRITERIAS] Evaluation periode : [EVAL_PERIOD] <br />Please connect yourself to the application : <a href="[SITE_URL]">[SITE_URL] </a> <br />\r\n<p>Thanking you in advance  for your collaboration, I send you my best regards.</p><p>Sincerely,<br />eMundus coordinator</p>', 'International Student Application Office'),
(13, 'teachers_set', 'eMundus | Learning agreement', 'info@emundus.fr', 'Dear [NAME],<br />\r\nYou have been affected to a list of selected applicants for learning agreement process.<br />Please connect yourself to the application : <a href="[SITE_URL]">[SITE_URL] </a> <br />Thanking you in advance  for your collaboration, I send you my best regards.\r\nSincerely,<br /> eMundus coordinator', 'International Student Application Office'),
(14, 'new_thesis', 'eMundus | A new thesis subject is available', 'info@emundus.fr', '<p>Dear [NAME],<br />\r\nThe thesis list has been updated. A new subject is now available.<br />\r\nThis subject is : [SUBJECT].<br />\r\n<p>Sincerely,<br />[SIGNATURE]', 'International Student Application Office'),
(15, 'reminder', 'eMundus | Application not finished', 'info@emundus.fr', '<p>Dear [NAME],<br />\r\nYour profile is not yet complete. Please complete it in order to finish your application.<br />\r\n<p>You can acces to our website by this adress:<a href=''[SITE_URL]''>[SITE_URL]</a>. Remember the deadline is the [DEADLINE].<br />\r\n<p>Sincerely,<br />[SIGNATURE]', 'International Student Application Office'),
(16, 'remind_report', 'Erasmus Mundus | Send your report', 'info@emundus.fr', '<p>Dear [NAME],<br />\nYou start the program on the [CANDIDATURE_START]. \nAt the end of your program don''t forget you must send a report to your administration.\n<br />\n<p>You can acces to our website by this adress:<a href=''[SITE_URL]''>[SITE_URL]</a>. \n<p>Sincerely,<br />[SIGNATURE]', 'International Student Application Office');
